def c2b(code):
	if code == 'I HAVE AN APPLE':
		return '+'
	elif code == 'I HAVE PINEAPPLE':
		return '-'
	elif code == 'APPLE-PEN':
		return '>'
	elif code == 'PINEAPPLE-PEN':
		return '<'
	elif code == 'I HAVE A PEN':
		return '.'
	elif code == 'UH':
		return ','
	elif code == 'PPAP':
		return '['
	elif code == 'PEN-PINEAPPLE-APPLE-PEN':
		return ']'
	else:
		raise Exception('Unknown code: {}'.format(code))

